// Dependencies
import { useRef, useState } from 'react';
import {
  Box,
  Button,
  InputGroup,
  InputRightElement,
  Stack
} from '@chakra-ui/core';

// Styles
import { FiLogIn } from 'react-icons/fi';
import { StyledButton, StyledInput } from './styles/styled';

// Component
import FormLayout from '../lib/FormLayout';

const SignInForm = () => {
  const ref = useRef('');
  const [status, setStatus] = useState(false);
  const [showPassword, setShowing] = useState(false);

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      <FormLayout>
        <Box>
          <StyledInput
            type="email"
            placeholder="Enter email"
            isDisabled={status}
          />
          <InputGroup size="md">
            <StyledInput
              pr="4.5rem"
              type={showPassword ? 'text' : 'password'}
              placeholder="Enter password"
              isDisabled={status}
            />
            <InputRightElement width="4.5rem">
              <Button h="1.75rem" size="sm" onClick={setShowing()}>
                {show ? 'Hide' : 'Show'}
              </Button>
            </InputRightElement>
          </InputGroup>
        </Box>
        <StyledButton
          type="submit"
          variantColor="blue"
          icon={FiLogIn}
          isLoading={status}
          isDisabled={status}
          aira-busy={status}
          height
        />
      </FormLayout>
    </Stack>
  );
};

export default SignInForm;
