import styled from '@emotion/styled';
import { IconButton, Input } from '@chakra-ui/core';

const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export { StyledButton, StyledInput };
