import Link from 'next/link';
import {
  Box,
  Button,
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  Text,
  useDisclosure
} from '@chakra-ui/core';

const SideInfo = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();

  return (
    <Box position="absolute" top="0" right="0">
      <Button ref={btnRef} variantColor="teal" onClick={onOpen}>
        About Me
      </Button>
      <Drawer
        isOpen={isOpen}
        placement="right"
        onClose={onClose}
        finalFocusRef={btnRef}
        size="md"
      >
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader size="md" borderBottomWidth="1px">
            About Me
          </DrawerHeader>

          <DrawerBody>
            <Text>Welcome to my grocery list web app!</Text>
            <Text>This is my attempt as using some new tech:</Text>
          </DrawerBody>
        </DrawerContent>
      </Drawer>
    </Box>
  );
};

export default SideInfo;
