import {
  SimpleGrid,
  ListItem,
  CloseButton,
  Flex,
  Text,
  IconButton
} from '@chakra-ui/core';
import { MdCheck } from 'react-icons/md';

const Item = ({ id, product, quantity, handleClick }) => {

  return (
    <ListItem>
      <SimpleGrid columns="3">
        <Text alignSelf="center" as="b">
          {product}
        </Text>
        <Text alignSelf="center" as="b">
          {quantity}
        </Text>
        <Flex justify="center">
            <CloseButton onClick={() => handleClick(id)} />
          ) : (
            <IconButton icon={MdCheck} />
          )}
        </Flex>
      </SimpleGrid>
    </ListItem>
  );
};
export default Item;
