// Dependnencies
import { useContext, useEffect } from 'react';
import { useMutation, useQuery } from '@apollo/react-hooks';

// Helpers
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { REMOVE_ITEM_MUTATION } from '../lib/apollo/mutations';
import { doctorError } from '../lib/doctorError';

// Styles
import {
  Box,
  Button,
  Heading,
  SimpleGrid,
  Stack,
  useToast
} from '@chakra-ui/core';

// React
import Item from './Item';
import Loading from './Loading';
import { ShoppingContext } from '../lib/React/ShoppingContext';

const GroceryList = () => {
  // Hooks + Contexts
  const toast = useToast();
  const { isShopping, setIsShopping } = useContext(ShoppingContext);

  // Apollo
  const { data, loading, error } = useQuery(USERS_ITEMS_QUERY);
  const [deleteItem, deleteResponse] = useMutation(REMOVE_ITEM_MUTATION);

  // Handlers
  const handleClick = async (id) => {
    try {
      await deleteItem({
        variables: { input: { id } },
        optimisticResponse: {
          __typename: 'Mutation',
          deleteItem: {
            item: { id }
          }
        },
        update: (cache, { data: { deleteItem } }) => {
          const { currentUserItems } = cache.readQuery({
            query: USERS_ITEMS_QUERY
          });
          const { nodes } = currentUserItems;
          const { id } = deleteItem.item;

          cache.writeQuery({
            query: USERS_ITEMS_QUERY,
            data: {
              currentUserItems: {
                nodes: nodes.filter((node) => node.id !== id)
              }
            }
          });
        }
      });
    } catch (error) {
      // need to catch the error, so a popup doesn't show up
    }
  };

  if (error || deleteResponse.error) {
    if (!!error) {
      toast({
        description: error,
        status: 'error',
        duration: 9001,
        isClosable: true
      });
    }

    if (!!deleteResponse.error) {
      toast({
        description: doctorError(deleteResponse.error.message),
        status: 'error',
        duration: 9001,
        isClosable: true
      });
    }
  }

  if (loading) return <Loading />;

  if (data.currentUserItems?.nodes.length) {
    const list = data.currentUserItems.nodes;

    return (
      <React.Fragment>
        <Button
          variantColor="blue"
          aria-label={`Checkout the selected items`}
          alignSelf="center"
          width="35%"
          maxWidth="300px"
          marginBottom="2rem"
        >
          Checkout
        </Button>
        <Stack
          width="60%"
          maxWidth="500px"
          minWidth="300px"
          margin="auto"
          spacing="0.5rem"
        >
          <SimpleGrid columns="3">
            <Heading as="h4" size="lg">
              Item
            </Heading>
            <Heading as="h4" size="lg">
              Quantity
            </Heading>
          </SimpleGrid>
          <Box>
            {list.map((item) => (
              <Item
                key={item.id + item.product}
                id={item.id}
                product={item.product}
                quantity={item.quantity}
                handleClick={handleClick}
              />
            ))}
          </Box>
        </Stack>
      </React.Fragment>
    );
  } else {
    return (
      <Heading as="h4" size="lg">
        Remember to add to your list. 📝
      </Heading>
    );
  }
};

export default GroceryList;
