import { useRef } from 'react';
import { Flex, Heading, Stack, Input, IconButton } from '@chakra-ui/core';

const GroceryForm = () => {
  const [product] = useRef('');
  const [quantity] = useRef('');

  return (
    <Stack spacing="2rem">
      <Heading as="h1" size="2xl">
        Add Items
      </Heading>
      <Flex>
        <form>
          <Input ref={product.value} />
          <Input ref={quantity.value} />
          <IconButton />
        </form>
      </Flex>
    </Stack>
  );
};

export default GroceryForm;
