import { Button, Flex, Grid } from '@chakra-ui/core';

const FormLayout = (props) => (
  <Flex justify="center" alignItems="center" margin="auto" width="75%">
    <form method="post" onSubmit={props.handleSubmit}>
      <Grid templateColumns="5fr 1fr" paddingBottom="0.5rem">
        {props.children}
      </Grid>
      <Flex as={Button} justifySelf="flex-end">
        Register
      </Flex>
    </form>
  </Flex>
);

export default FormLayout;
