import styled from '@emotion/styled';
import { AccordionIcon, IconButton, Input } from '@chakra-ui/core';

export const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInputPW = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledAccordionIcon = styled(AccordionIcon)`
  :focus {
    box-shadow: 0, 0, 0, 0;
  }
`;
