import { useQuery } from '@apollo/react-hooks';
import { ALL_ITEMS_QUERY } from '../lib/queries';
import Item from './Item';

const GroceryList = () => {
  const { data, loading, error } = useQuery(ALL_ITEMS_QUERY);

  if (error) return <p>Error: {error}</p>;
  if (loading)
    return (
      <div>
        <p>Loading...</p>
      </div>
    );

  const items = data.items.nodes;

  return (
    <div>
      <ul>
        {items.map((item) => (
          <Item key={item.id} product={item.product} quantity={item.quantity} />
        ))}
      </ul>
    </div>
  );
};

export default GroceryList;
