// Dependencies
import { useState } from 'react';
import { formatDistance } from 'date-fns';

// Styles
import {
  Accordion,
  AccordionItem,
  AccordionPanel,
  AccordionIcon,
  Box,
  Text
} from '@chakra-ui/core';
import { StyledAcc, StyleAccHeader } from './styles/styled';
import HistoryItem from './HistoryItem';

const HistoryList = ({ id, history, createdAt }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <React.Fragment>
      <StyledAcc
        background="white"
        border="1.5px solid black"
        borderRadius="1rem"
        marginBottom="1rem"
      >
        <AccordionItem>
          <StyleAccHeader onClick={() => setIsOpen(!isOpen)}>
            <Box flex="1" textAlign="left">
              <Text fontSize="2xl">Order No. {id}</Text>
              <Text fontSize="lg">
                {formatDistance(Date.parse(createdAt), new Date(), {
                  addSuffix: true
                })}
              </Text>
            </Box>
            <AccordionIcon />
          </StyleAccHeader>
          <AccordionPanel pb={4} isOpen={isOpen}>
            {history.map((item, index) => (
              <HistoryItem key={index} {...item} />
            ))}
          </AccordionPanel>
        </AccordionItem>
      </StyledAcc>
    </React.Fragment>
  );
};

export default HistoryList;
