// Dependencies
import { useContext, useEffect, useRef, useState } from 'react';
import Router from 'next/router';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Box, Flex, Grid, IconButton, Stack } from '@chakra-ui/core';
import { MdAddShoppingCart, MdReceipt } from 'react-icons/md';

// Helpers
import { ADD_ITEM_MUTATION } from '../lib/apollo/mutations';
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { StyledButton, StyledInput } from './styles/styled';

// Contexts
import { UserContext } from '../lib/React/UserContext';
import { ViewContext } from '../lib/React/ViewContext';

const GroceryForm = () => {
  // Form Refs
  const productRef = useRef('');
  const quantityRef = useRef('');

  // Hooks
  const { user, setUser } = useContext(UserContext);
  const { isAdd, setIsAdd } = useContext(ViewContext);
  const [status, setStatus] = useState(false);

  // Apollo
  const [addItem, itemRes] = useMutation(ADD_ITEM_MUTATION);

  // useEffect
  useEffect(() => {
    if (!user) Router.push('/signin');
  }, []);

  // Handleres
  const handleClick = () => {
    setIsAdd(!isAdd);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus(true);

    const input = {
      product: productRef.current.value,
      quantity: parseInt(quantityRef.current.value)
    };

    addItem({
      variables: { input },
      optimisticResponse: {
        __typename: 'Mutation',
        addItem: {
          item: {
            id: Math.round(Math.random() * -1000000),
            ...input,
            status: false
          }
        }
      },
      update: (cache, { data: { addItem } }) => {
        const { currentUser } = cache.readQuery({ query: USERS_ITEMS_QUERY });
        const { nodes } = currentUser.itemsByOwnerId;
        const { item } = addItem;

        cache.writeQuery({
          query: USERS_ITEMS_QUERY,
          data: { currentUser: { itemsByOwnerId: { nodes: [item, ...nodes] } } }
        });
      }
    });

    setStatus(false);
    productRef.current.value = '';
    quantityRef.current.value = '';

    productRef.current.focus();
  };

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      {isAdd ? (
        <IconButton
          variantColor="blue"
          icon={MdAddShoppingCart}
          width="65%"
          maxWidth="300px"
          alignSelf="center"
          onClick={handleClick}
          aria-label="Check to switch from add to remove"
        />
      ) : (
        <IconButton
          variantColor="blue"
          icon={MdReceipt}
          width="65%"
          maxWidth="300px"
          alignSelf="center"
          onClick={handleClick}
          aria-label="Check to switch from remove to add"
        />
      )}
      {isAdd && (
        <Flex justify="center">
          <form method="post" onSubmit={handleSubmit}>
            <Grid templateColumns="5fr 1fr">
              <Box>
                <StyledInput
                  type="text"
                  placeholder="Product"
                  isDisabled={status}
                  ref={productRef}
                />
                <StyledInput
                  type="number"
                  placeholder="Quantity"
                  isDisabled={status}
                  ref={quantityRef}
                />
              </Box>
              <StyledButton
                type="submit"
                variantColor="blue"
                icon={MdAddShoppingCart}
                isLoading={status}
                isDisabled={status}
                aira-busy={status}
                height
              />
            </Grid>
          </form>
        </Flex>
      )}
    </Stack>
  );
};

export default GroceryForm;
