// Dependencies
import React, { useContext, useState, useRef } from 'react';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Box, Flex, Grid, IconButton, Stack } from '@chakra-ui/core';
import { MdAddShoppingCart, MdReceipt } from 'react-icons/md';

// Helpers
import { ViewContext } from '../lib/ViewContext';
import { ADD_ITEM_MUTATION } from '../lib/mutations';
import { ALL_ITEMS_QUERY } from '../lib/queries';
import { StyledButton, StyledInput } from './styles/styled';

// Components
import FormLayout from '../lib/FormLayout';

const GroceryForm = () => {
  const productRef = useRef('');
  const quantityRef = useRef('');
  const [status, setStatus] = useState(false);
  const { isAdd, setIsAdd } = useContext(ViewContext);

  const [createItem, itemRes] = useMutation(ADD_ITEM_MUTATION, {
    update(cache, { data: { createItem } }) {
      const {
        items: { nodes }
      } = cache.readQuery({ query: ALL_ITEMS_QUERY });

      const item = createItem.item;

      cache.writeQuery({
        query: ALL_ITEMS_QUERY,
        data: { items: { nodes: [item, ...nodes] } }
      });
    }
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus(true);

    const input = {
      item: {
        product: productRef.current.value,
        quantity: parseInt(quantityRef.current.value)
      }
    };

    createItem({ variables: { input } });

    setStatus(false);
    productRef.current.value = '';
    quantityRef.current.value = '';

    productRef.current.focus();
  };

  const handleClick = () => {
    setIsAdd(!isAdd);
  };

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      {isAdd ? (
        <IconButton
          variantColor="blue"
          icon={MdAddShoppingCart}
          width="65%"
          maxWidth="300px"
          alignSelf="center"
          onClick={handleClick}
          aria-label="Check to switch from add to remove"
        />
      ) : (
        <IconButton
          variantColor="blue"
          icon={MdReceipt}
          width="65%"
          maxWidth="300px"
          alignSelf="center"
          onClick={handleClick}
          aria-label="Check to switch from remove to add"
        />
      )}
      {isAdd && (
        <Flex justify="center">
          <form method="post" onSubmit={handleSubmit}>
            <Grid templateColumns="5fr 1fr">
              <Box>
                <StyledInput
                  type="text"
                  placeholder="Product"
                  isDisabled={status}
                  ref={productRef}
                />
                <StyledInput
                  type="number"
                  placeholder="Quantity"
                  isDisabled={status}
                  ref={quantityRef}
                />
              </Box>
              <StyledButton
                type="submit"
                variantColor="blue"
                icon={MdAddShoppingCart}
                isLoading={status}
                isDisabled={status}
                aira-busy={status}
                height
              />
            </Grid>
          </form>
        </Flex>
      )}
    </Stack>
  );
};

export default GroceryForm;
