// Dependencies
import { useState } from 'react';
import { formatDistance, subDays } from 'date-fns';

// Styles
import {
  Accordion,
  AccordionItem,
  AccordionPanel,
  AccordionIcon,
  Box,
  Text
} from '@chakra-ui/core';
import { StyleAccHeader } from './styles/styled';

const HistoryList = ({ id, history, createdAt }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Accordion
      background="white"
      border="1px solid black"
      borderRadius="1rem"
      marginBottom="1rem"
    >
      <AccordionItem>
        <StyleAccHeader onClick={() => setIsOpen(!isOpen)}>
          <Box flex="1" textAlign="left">
            <Text fontSize="2xl">Order No. {id}</Text>
            <Text fontSize="lg">
              {formatDistance(new Date(), Date.parse(createdAt), {
                addSuffix: false
              })}
            </Text>
          </Box>
          <AccordionIcon />
        </StyleAccHeader>
        <AccordionPanel pb={4} isOpen={isOpen}>
          {history.map((item) => {
            console.log(item);
          })}
        </AccordionPanel>
      </AccordionItem>
    </Accordion>
  );
};

export default HistoryList;
