import { useMutation } from '@apollo/react-hooks';
import { REMOVE_ITEM_MUTATION } from '../lib/mutations';
import { ALL_ITEMS_QUERY } from '../lib/queries';
import { List, ListItem, SimpleGrid, Heading } from '@chakra-ui/core';
import Item from './Item';

const GroceryList = () => {
  const [deleteItem, deleteRes] = useMutation(REMOVE_ITEM_MUTATION, {
    update(cache, { data: { deleteItem } }) {
      const {
        items: { nodes }
      } = cache.readQuery({ query: ALL_ITEMS_QUERY });

      const { id } = deleteItem.item;
      cache.writeQuery({
        query: ALL_ITEMS_QUERY,
        data: { items: { nodes: nodes.filter((node) => node.id !== id) } }
      });
    }
  });

  const handleClick = (id) => {
    deleteItem({ variables: { input: { id } } });
  };

  const items = [];

  if (items.length > 0) {
    return (
      <List width="60%" maxWidth="500px" minWidth="300px" margin="auto">
        <ListItem paddingBottom="0.5rem">
          <SimpleGrid columns="3">
            <Heading as="h4" size="lg">
              Item
            </Heading>
            <Heading as="h4" size="lg">
              Quantity
            </Heading>
          </SimpleGrid>
        </ListItem>
        {items.map((item) => (
          <Item
            key={item.id + item.product}
            id={item.id}
            product={item.product}
            quantity={item.quantity}
            handleClick={handleClick}
          />
        ))}
      </List>
    );
  } else {
    return (
      <Heading as="h4" size="lg">
        No groceries.
      </Heading>
    );
  }
};

export default GroceryList;
