// Dependencies
import Link from 'next/link';
import { useRouter } from 'next/router';
import Cookies from 'js-cookie';

// Styles
import { Flex, List, ListItem, Heading } from '@chakra-ui/core';

// Components
import NavSkeleton from './NavSkeleton';

const Nav = () => {
  const { pathname } = useRouter();

  return (
    <List as={Flex} justifyContent="space-evenly" paddingBottom="2rem">
      <NavSkeleton endpoint={'history'} />
      <NavSkeleton endpoint={'home'} />
      <ListItem>
        {pathname === '/' ? (
          <Heading as="h1" size="2xl" as="u" cursor="default">
            Home
          </Heading>
        ) : (
          <Link href="/">
            <a>
              <Heading as="h1" size="2xl">
                Home
              </Heading>
            </a>
          </Link>
        )}
      </ListItem>
      {Cookies.get('grocerylist_auth_token') ? (
        <ListItem>
          <Heading
            as="h1"
            size="2xl"
            cursor="pointer"
            onClick={() => {
              Cookies.remove('grocerylist_auth_token');
            }}
          >
            Sign Out
          </Heading>
        </ListItem>
      ) : (
        <ListItem>
          <Heading as="h1" size="2xl" cursor="pointer">
            Sign In
          </Heading>
        </ListItem>
      )}
    </List>
  );
};

export default Nav;
