// Dependencies
import Link from 'next/link';
import { useRouter } from 'next/router';

// Components
import Nav from './Nav';

const Header = (props) => {
  const { pathname } = useRouter();

  return (
    <React.Fragment>
      {pathname === '/' || pathname === '/signin' ? (
        <img src="/grocery-bags.png" alt="Image of Grocery Bags" />
      ) : (
        <Link href="/">
          <a>
            <img src="/grocery-bags.png" alt="Image of Grocery Bags" />
          </a>
        </Link>
      )}
      <Nav {...props} />
    </React.Fragment>
  );
};

export default Header;
