// Dependencies
import React, { useState, useRef } from 'react';
import { useMutation } from '@apollo/react-hooks';
import styled from '@emotion/styled';

// Styles
import {
  Box,
  Flex,
  Grid,
  Heading,
  Input,
  IconButton,
  Stack
} from '@chakra-ui/core';
import { MdAddShoppingCart } from 'react-icons/md';

// Styled
const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

// Helpers
import { ADD_ITEM_MUTATION } from '../lib/mutations';

const GroceryForm = () => {
  const productRef = useRef('');
  const quantityRef = useRef('');
  const [status, setStatus] = useState(false);
  const [createItem, itemRes] = useMutation(ADD_ITEM_MUTATION);

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus(true);

    const input = {
      item: {
        product: productRef.current.value,
        quantity: quantityRef.current.value
      }
    };

    createItem({ variables: input });
    setStatus(false);
    productRef.current.value = '';
    quantityRef.current.value = '';
  };

  if (itemRes.data) console.log(itemRes.data);
  if (itemRes.data) console.log(itemRes.data);

  return (
    <Stack spacing="2rem">
      <Heading as="h1" size="2xl">
        Add Items
      </Heading>
      <Flex justify="center" paddingBottom="2rem">
        <form method="post" onSubmit={handleSubmit}>
          <Grid templateColumns="5fr 1fr">
            <Box>
              <StyledInput
                type="text"
                placeholder="Product"
                isDisabled={status}
                ref={productRef}
              />
              <StyledInput
                type="number"
                placeholder="Quantity"
                isDisabled={status}
                ref={quantityRef}
              />
            </Box>
            <StyledButton
              type="submit"
              icon={MdAddShoppingCart}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              height
            />
          </Grid>
        </form>
      </Flex>
    </Stack>
  );
};

export default GroceryForm;
