import styled from '@emotion/styled';
import {
  Accordion,
  AccordionHeader,
  AccordionIcon,
  AccordionItem,
  Box,
  IconButton,
  Input
} from '@chakra-ui/core';

export const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInputPW = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledAccordion = styled(Accordion)`
  :focus {
    box-shadow: 0, 0, 0, 0;
  }
`;

export const StyledAccordionHeader = styled(AccordionHeader)`
  :focus {
    box-shadow: 0, 0, 0, 0;
  }
`;

export const StyledAccordionIcon = styled(AccordionIcon)`
  &:focus {
    box-shadow: 0, 0, 0, 0;
  }
`;

export const StyledAccordionItem = styled(AccordionItem)`
  &:focus {
    box-shadow: 0, 0, 0, 0;
  }
`;

export const StyledBox = styled(Box)`
  *:focus {
    box-shadow: 0, 0, 0, 0;
  }
`;
