import { useContext, useState } from 'react';
import { SimpleGrid, ListItem, Flex, Text, IconButton } from '@chakra-ui/core';
import { MdRemoveCircleOutline, MdLabel, MdLabelOutline } from 'react-icons/md';
import { ViewContext } from '../lib/ViewContext';

const Item = ({ id, product, quantity, handleClick }) => {
  const { isAdd, setIsAdd } = useContext(ViewContext);
  const [selected, setSelected] = useState(false);

  return (
    <ListItem paddingBottom="0.5rem">
      <Flex justify="center">
        {isAdd ? (
          <IconButton
            size="sm"
            variantColor="blue"
            icon={MdRemoveCircleOutline}
            onClick={() => handleClick(id)}
            aria-label="Click to remove from list"
          />
        ) : (
          <IconButton
            size="sm"
            variantColor="blue"
            icon={selected ? MdLabelOutline : MdLabel}
            onClick={() => setSelected(!selected)}
            aria-label={`Click to select`}
          />
        )}
      </Flex>
      <SimpleGrid columns="3">
        <Text alignSelf="center" as="b">
          {product}
        </Text>
        <Text alignSelf="center" as="b">
          {quantity}
        </Text>
      </SimpleGrid>
    </ListItem>
  );
};
export default Item;
