// Dependencies
import { useRef, useState } from 'react';
import { useMutation } from '@apollo/react-hooks';
import Cookies from 'js-cookie';
import {
  Button,
  Flex,
  Heading,
  InputGroup,
  InputRightElement,
  SimpleGrid,
  Stack
} from '@chakra-ui/core';

// Styles
import { AiOutlineForm, AiOutlineDoubleLeft } from 'react-icons/ai';
import { FiLogIn } from 'react-icons/fi';
import { StyledButton, StyledInput, StyledInputPW } from './styles/styled';

// Helpers
import { REGISTER_USER_MUTATION, SIGNIN_USER_MUTATION } from '../lib/mutations';

const SignInForm = () => {
  // Form Refs
  const emailRef = useRef('');
  const nameRef = useRef('');
  const passwordRef = useRef('');
  const passwordConfirmationRef = useRef('');

  // Hooks
  const [status, setStatus] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShow] = useState(false);
  const [registering, setRegistering] = useState(false);

  // Apollo
  const [register, registerRes] = useMutation(REGISTER_USER_MUTATION);
  const [signin, signinRes] = useMutation(SIGNIN_USER_MUTATION);

  const toggleRegister = () => {
    setShow(!showPassword);
  };

  const handleSubmit = async (e) => {
    setStatus(true);
    e.preventDefault();

    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    if (registering) {
      const displayname = nameRef.current.value;
      const confirmpassword = passwordConfirmationRef.current.value;

      try {
        await register({
          variables: {
            input: { email, displayname, password, confirmpassword }
          }
        });
        const tokenRes = await signin({
          variables: { input: { email, password } }
        });
        setError('');

        console.log('tokenRes');
        console.log(tokenRes);
      } catch (error) {
        setError(error.message.replace('GraphQL error: ', ''));
      }
    } else {
      try {
        const tokenRes = await signin({
          variables: { input: { email, password } }
        });
        setError('');

        console.log(tokenRes);
      } catch (error) {
        setError(error.message.replace('GraphQL error: ', ''));
      }
    }

    setStatus(false);
  };

  return (
    <Stack spacing="0.5rem" paddingBottom="2rem">
      {error !== '' && (
        <Heading as="h5" size="sm" color="red.600">
          {error}
        </Heading>
      )}
      <Flex
        justify="center"
        flexDirection="column"
        margin="auto"
        minWidth="300px"
        maxWidth="400px"
      >
        <form method="post" onSubmit={handleSubmit}>
          <StyledInput
            type="email"
            placeholder="Enter email"
            ref={emailRef}
            isDisabled={status}
          />

          {registering && (
            <StyledInput
              type="text"
              placeholder="Enter display name"
              ref={nameRef}
              isDisabled={status}
            />
          )}

          <InputGroup size="md">
            <StyledInputPW
              pr="4.5rem"
              type={showPassword ? 'text' : 'password'}
              placeholder="Enter password"
              ref={passwordRef}
              isDisabled={status}
              error={error}
            />
            <InputRightElement width="4.5rem">
              <Button h="1.75rem" size="sm" onClick={toggleRegister}>
                {showPassword ? 'Hide' : 'Show'}
              </Button>
            </InputRightElement>
          </InputGroup>

          {registering && (
            <InputGroup size="md">
              <StyledInputPW
                pr="4.5rem"
                type={showPassword ? 'text' : 'password'}
                placeholder="Confirm password"
                ref={passwordConfirmationRef}
                isDisabled={status}
                error={error}
              />
              <InputRightElement width="4.5rem">
                <Button h="1.75rem" size="sm" onClick={toggleRegister}>
                  {showPassword ? 'Hide' : 'Show'}
                </Button>
              </InputRightElement>
            </InputGroup>
          )}

          <SimpleGrid columns="2" gridGap="5px">
            <StyledButton
              variantColor="blue"
              icon={registering ? AiOutlineDoubleLeft : AiOutlineForm}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              aria-label="Register for an account"
              onClick={() => setRegistering(!registering)}
            />
            <StyledButton
              type="submit"
              variantColor="blue"
              icon={FiLogIn}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              aria-label="Log into your account"
            />
          </SimpleGrid>
        </form>
      </Flex>
    </Stack>
  );
};

export default SignInForm;
