// Dependencies
import { useState } from 'react';
import moment from 'moment';

// Styles
import {
  Accordion,
  AccordionItem,
  AccordionHeader,
  AccordionPanel,
  AccordionIcon,
  Box,
  Text,
  SimpleGrid,
  Flex
} from '@chakra-ui/core';
import {
  StyledAccordionHeader,
  StyledAccordionIcon,
  StyledAccordionItem,
  StyledBox
} from './styles/styled';

const HistoryList = ({ id, history, createdAt }) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <Accordion background="white" border="1px solid black" borderRadius="1rem">
      <StyledAccordionItem>
        <StyledAccordionHeader onClick={() => setIsOpen(!isOpen)}>
          <StyledBox flex="1" textAlign="left">
            <Text fontSize="2xl">Order No. {id}</Text>
            <Text fontSize="lg">{moment(createdAt, 'YYYYMMDD').fromNow()}</Text>
          </StyledBox>
          <StyledAccordionIcon />
        </StyledAccordionHeader>
        <AccordionPanel pb={4} isOpen={isOpen}>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat.
        </AccordionPanel>
      </StyledAccordionItem>
    </Accordion>
  );
};

export default HistoryList;
