// Dependencies
import React, { useState, useRef } from 'react';
import { useMutation } from '@apollo/react-hooks';
import styled from '@emotion/styled';

// Styles
import {
  Box,
  Flex,
  Grid,
  Heading,
  Input,
  IconButton,
  Stack
} from '@chakra-ui/core';
import { MdAddShoppingCart, MdRemoveShoppingCart } from 'react-icons/md';

// Styled
const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

// Helpers
import useView from '../lib/useView';
import { ADD_ITEM_MUTATION } from '../lib/mutations';
import { ALL_ITEMS_QUERY } from '../lib/queries';

const GroceryForm = () => {
  const productRef = useRef('');
  const quantityRef = useRef('');
  const [status, setStatus] = useState(false);
  const [isAdd, setIsAdd] = useView();
  const [createItem, itemRes] = useMutation(ADD_ITEM_MUTATION, {
    update(cache, { data: { createItem } }) {
      const {
        items: { nodes }
      } = cache.readQuery({ query: ALL_ITEMS_QUERY });

      const item = createItem.item;

      cache.writeQuery({
        query: ALL_ITEMS_QUERY,
        data: { items: { nodes: [item, ...nodes] } }
      });
    }
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus(true);

    const input = {
      item: {
        product: productRef.current.value,
        quantity: parseInt(quantityRef.current.value)
      }
    };

    createItem({ variables: { input } });

    setStatus(false);
    productRef.current.value = '';
    quantityRef.current.value = '';


  };

  const handleClick = () => {
    setIsAdd(!isAdd);
  };

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      {isAdd ? (
        <IconButton
          icon={MdAddShoppingCart}
          width="65%"
          alignSelf="center"
          onClick={handleClick}
        />
      ) : (
        <IconButton
          icon={MdRemoveShoppingCart}
          width="65%"
          alignSelf="center"
          onClick={handleClick}
        />
      )}
      {isAdd && (
        <Flex justify="center">
          <form method="post" onSubmit={handleSubmit}>
            <Grid templateColumns="5fr 1fr">
              <Box>
                <StyledInput
                  type="text"
                  placeholder="Product"
                  isDisabled={status}
                  ref={productRef}
                />
                <StyledInput
                  type="number"
                  placeholder="Quantity"
                  isDisabled={status}
                  ref={quantityRef}
                />
              </Box>
              <StyledButton
                type="submit"
                icon={MdAddShoppingCart}
                isLoading={status}
                isDisabled={status}
                aira-busy={status}
                height
              />
            </Grid>
          </form>
        </Flex>
      )}
    </Stack>
  );
};

export default GroceryForm;
