import { SimpleGrid, ListItem, CloseButton, Flex, Text } from '@chakra-ui/core';

const Item = (props) => {
  return (
    <ListItem>
      <SimpleGrid columns="3">
        <Text alignSelf="center" as="b">
          {props.product}
        </Text>
        <Text alignSelf="center" as="b">
          {props.quantity}
        </Text>
        <Flex justify="center">
          <CloseButton />
        </Flex>
      </SimpleGrid>
    </ListItem>
  );
};

export default Item;
