// Dependencies
import Link from 'next/link';
import { useRouter } from 'next/router';
import Cookies from 'js-cookie';

// Styles
import { Flex, List, ListItem, Heading } from '@chakra-ui/core';

const NavSkeleton = (props) => {
  const { pathname } = useRouter();

  console.log('NavSkeleton Props');
  console.log(props);

  return (
    <ListItem>
      {pathname === `${props.endpoint}` ? (
        <Heading as="h1" size="2xl" as="u" cursor="default">
          {props.endpoint[0].toUpperCase()}
          {props.endpoint.slice(1)}
        </Heading>
      ) : (
        <Link href={`${props.endpoint}`}>
          <a>
            <Heading as="h1" size="2xl">
              {props.endpoint[0].toUpperCase()}
              {props.endpoint.slice(1)}
            </Heading>
          </a>
        </Link>
      )}
    </ListItem>
  );
};

export default NavSkeleton;
