// Dependencies
import Link from 'next/link';
import { useRouter } from 'next/router';

// Styles
import { Flex, List, ListItem, Heading } from '@chakra-ui/core';

const Nav = () => {
  const { pathname } = useRouter();

  return (
    <List as={Flex} justifyContent="space-evenly" paddingBottom="2rem">
      <ListItem>
        {pathname === '/history' ? (
          <Heading as="h1" size="2xl" as="u" cursor="default">
            History
          </Heading>
        ) : (
          <Link href="/history">
            <a>
              <Heading as="h1" size="2xl">
                History
              </Heading>
            </a>
          </Link>
        )}
      </ListItem>
      <ListItem>
        {pathname === '/' ? (
          <Heading as="h1" size="2xl" as="u" cursor="default">
            Home
          </Heading>
        ) : (
          <Link href="/">
            <a>
              <Heading as="h1" size="2xl">
                Home
              </Heading>
            </a>
          </Link>
        )}
      </ListItem>
      <ListItem>
        <Heading as="h1" size="2xl" as="u" cursor="pointer">
          History
        </Heading>
      </ListItem>
    </List>
  );
};

export default Nav;
