import styled from '@emotion/styled';
import { IconButton, Input } from '@chakra-ui/core';

export const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInputPW = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const InputField = ({ text, placeholder, isCalling }) => {
  return (
    <StyledInput
      type={props.text ? props.text : 'text'}
      placeholder={props.placeholder ? props.placeholder : 'Product'}
      isDisabled={props.isCalling}
      isRequired
    />
  );
};
