// Dependencies
import Link from 'next/link';
import { useRouter } from 'next/router';

// Styles
import { Heading } from '@chakra-ui/core';

const NavSkeleton = ({ endpoint, title }) => {
  const { pathname } = useRouter();

  if (endpoint !== '/') {
    return (
      <React.Fragment>
        {pathname === endpoint ? (
          <Heading as="h1" size="2xl" as="u" cursor="default">
            {title}
          </Heading>
        ) : (
          <Link href={endpoint}>
            <a>
              <Heading as="h1" size="2xl">
                {title}
              </Heading>
            </a>
          </Link>
        )}
      </React.Fragment>
    );
  } else {
    return (
      <React.Fragment>
        {pathname === endpoint || pathname ? (
          <Heading as="h1" size="2xl" as="u" cursor="default">
            {title}
          </Heading>
        ) : (
          <Link href={endpoint}>
            <a>
              <Heading as="h1" size="2xl">
                {title}
              </Heading>
            </a>
          </Link>
        )}
      </React.Fragment>
    );
  }
};

export default NavSkeleton;
