// Dependencies
import { useContext, useState } from 'react';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Button } from '@chakra-ui/core';

// Apollo
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { ADD_TO_HISTORY_MUTATION } from '../lib/apollo/mutations';

// React
import { StatusContext } from '../lib/React/StatusContext';

const GroceryCheckout = () => {
  // Hooks + Contexts
  const [isCalling, setIsCalling] = useState(false);
  const { status } = useContext(StatusContext);

  // Apollo
  const [addToHistory, addToHistoryResponse] = useMutation(
    ADD_TO_HISTORY_MUTATION
  );

  // Handlers
  const handleCheckout = async (e) => {
    e.preventDefault();
    setIsCalling(true);

    const input = { products: status };

    try {
      await addToHistory({
        variables: { input: { products: status } }
      });
    } catch (error) {
      console.log(JSON.parse(JSON.stringify(error)));
    }

    setIsCalling(false);
  };

  return (
    <form method="post" onSubmit={handleCheckout}>
      <Button
        type="sumbit"
        variantColor="blue"
        alignSelf="center"
        width="35%"
        maxWidth="300px"
        marginBottom="2rem"
        isLoading={isCalling}
        isDisabled={isCalling}
        aira-busy={isCalling}
        aria-label={`Checkout the selected items`}
      >
        Checkout
      </Button>
    </form>
  );
};

export default GroceryCheckout;
