import { ApolloClient } from 'apollo-client';
import { InMemoryCache } from 'apollo-cache-inmemory';
import { HttpLink } from 'apollo-link-http';
import { setContext } from 'apollo-link-context';
import Cookies from 'js-cookie';

const getToken = () => {
  console.log(Cookies.get('grocerylist_auth_token'));
  return Cookies.get('grocerylist_auth_token')
    ? Cookies.get('grocerylist_auth_token')
    : false;
};

const httpLink = createHttpLink({
  uri: '/graphql'
});

const authLink = setContext((_, { headers }) => {
  // get the authentication token from local storage if it exists
  const token = Cookies.get('grocerylist_auth_token');
  // return the headers to the context so httpLink can read them
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : null
    }
  };
});

export default function createApolloClient(initialState, ctx) {
  // The `ctx` (NextPageContext) will only be present on the server.
  // use it to extract auth headers (ctx.req) or similar.
  return new ApolloClient({
    ssrMode: Boolean(ctx),
    link: new HttpLink({
      // includeExtensions: true,
      uri: 'http://localhost:4000/graphiql', // Server URL (must be absolute)
      headers: {
        ...(getToken() ? { authorization: `Bearer ${getToken()}` } : {})
      }
    }),
    cache: new InMemoryCache({
      addTypename: false
    })
  });
}
