// Dependencies
import { useQuery } from '@apollo/react-hooks';

// Styles
import {
  Box,
  Heading,
  Skeleton,
  SimpleGrid,
  Stack,
  useToast
} from '@chakra-ui/core';

// Helpers
import { doctorError } from '../lib/doctorError';

// Apollo
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';

const HistoryList = () => {
  // Apollo
  const { data, loading, error } = useQuery(USERS_ITEMS_QUERY);

  if (error) {
    toast({
      description: error,
      status: 'error',
      duration: 9001,
      isClosable: true
    });
  }

  if (loading)
    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <Heading as="h4" size="lg">
          Loading
        </Heading>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
      </Stack>
    );

  return <div>Something here</div>;
};

export default HistoryList;
