// Dependencies
import { useRef, useState } from 'react';
import { Box, Stack } from '@chakra-ui/core';

// Styles
import { FiLogIn } from 'react-icons/fi';
import { StyledButton, StyledInput } from './styles/styled';

// Component
import FormLayout from '../lib/FormLayout';

const SignInForm = () => {
  const ref = useRef('');
  const [status, setStatus] = useState(false);

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      <FormLayout>
        <Box>
          <StyledInput type="text" placeholder="Email" isDisabled={status} />
          <StyledInput
            type="number"
            placeholder="Password"
            isDisabled={status}
          />
        </Box>
        <StyledButton
          type="submit"
          variantColor="blue"
          icon={FiLogIn}
          isLoading={status}
          isDisabled={status}
          aira-busy={status}
          height
        />
      </FormLayout>
    </Stack>
  );
};

export default SignInForm;
