// Styles
import { Heading } from '@chakra-ui/core';

// Helpers
const adjustError = (errorString) => {
  let fixedString;

  fixedString = errorString.replace('GraphQL error: ', '');
  console.log('fixedString');
  console.log(fixedString);

  return fixedString;
};

const ErrorMessage = (props) => {
  const string = adjustError(props.error);

  return (
    <Heading as="h5" size="sm" color="red.600" paddingBottom="0.25rem">
      {string}
    </Heading>
  );
};

export default ErrorMessage;
