// Dependnencies
import { useMutation, useQuery } from '@apollo/react-hooks';

// Helpers
import { ALL_ITEMS_QUERY } from '../lib/apollo/queries';
import { REMOVE_ITEM_MUTATION } from '../lib/apollo/mutations';

// Styles
import { Heading, Skeleton, SimpleGrid, Stack } from '@chakra-ui/core';

// Components
import Item from './Item';

const GroceryList = () => {
  // Apollo
  const { data, loading, error } = useQuery(ALL_ITEMS_QUERY);
  const [deleteItem, deleteRes] = useMutation(REMOVE_ITEM_MUTATION, {
    update(cache, { data: { deleteItem } }) {
      const {
        items: { nodes }
      } = cache.readQuery({ query: ALL_ITEMS_QUERY });

      const { id } = deleteItem.item;
      cache.writeQuery({
        query: ALL_ITEMS_QUERY,
        data: { items: { nodes: nodes.filter((node) => node.id !== id) } }
      });
    }
  });

  // Handlers
  const handleClick = (id) => {
    deleteItem({ variables: { input: { id } } });
  };

  if (deleteRes) console.log(JSON.parse(JSON.stringify(deleteRes)));

  if (error) {
    console.log('error:', JSON.parse(JSON.stringify(error)));
    return <div>Some error exists</div>;
  }

  if (loading)
    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <Heading as="h4" size="lg">
          Loading
        </Heading>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
      </Stack>
    );

  if (data.items.nodes.length) {
    const list = data.items.nodes;

    return (
      <React.Fragment>
        <Stack
          width="60%"
          maxWidth="500px"
          minWidth="300px"
          margin="auto"
          spacing="0.5rem"
        >
          <SimpleGrid columns="3">
            <Heading as="h4" size="lg">
              Item
            </Heading>
            <Heading as="h4" size="lg">
              Quantity
            </Heading>
          </SimpleGrid>
          <SimpleGrid columns="3">
            {list.map((item) => (
              <Item
                key={item.id + item.product}
                id={item.id}
                product={item.product}
                quantity={item.quantity}
                handleClick={handleClick}
              />
            ))}
          </SimpleGrid>
        </Stack>
      </React.Fragment>
    );
  } else {
    return (
      <React.Fragment>
        <Heading as="h4" size="lg">
          Remember to add to your list. 📝
        </Heading>
      </React.Fragment>
    );
  }
};

export default GroceryList;
