// Dependencies
import { useContext, useEffect, useRef, useState } from 'react';
import Router from 'next/router';
import { useMutation } from '@apollo/react-hooks';
import Cookies from 'js-cookie';

// Styles
import {
  Button,
  Flex,
  InputGroup,
  InputRightElement,
  SimpleGrid,
  Stack,
  useToast
} from '@chakra-ui/core';
import { AiOutlineForm, AiOutlineDoubleLeft } from 'react-icons/ai';
import { FiLogIn } from 'react-icons/fi';
import { StyledButton, StyledInput, StyledInputPW } from './styles/styled';

// Helpers
import {
  REGISTER_USER_MUTATION,
  SIGNIN_USER_MUTATION
} from '../lib/apollo/mutations';
import { doctorError } from '../lib/doctorError';

// Contexts
import { UserContext } from '../lib/React/UserContext';

const SignInForm = () => {
  // Form Refs
  const emailRef = useRef('');
  const nameRef = useRef('');
  const passwordRef = useRef('');
  const passwordConfirmationRef = useRef('');

  // Hooks
  const toast = useToast();
  const { user, setUser } = useContext(UserContext);
  const [status, setStatus] = useState(false);
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);

  // Apollo
  const [register, registerRes] = useMutation(REGISTER_USER_MUTATION);
  const [signin, signinRes] = useMutation(SIGNIN_USER_MUTATION);

  // useEffect
  useEffect(() => {
    if (user) Router.push('/');

    return location.reload();
  }, [user]);

  // Handlers
  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const toggleRegister = () => {
    setError('');
    setIsRegistering(!isRegistering);
  };

  const handleSubmit = async (e) => {
    setError('');
    setStatus(true);
    e.preventDefault();

    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    try {
      if (isRegistering) {
        const displayname = nameRef.current.value;
        const confirmpassword = passwordConfirmationRef?.current.value;

        await register({
          variables: {
            input: { email, displayname, password, confirmpassword }
          }
        });
      }

      const tokenRes = await signin({
        variables: { input: { email, password } }
      });

      // No cookies are set on an invalid login
      Cookies.set(
        'grocerylist_auth_token',
        tokenRes.data.authenticate.jwtToken
      );

      setStatus(false);
      setIsRegistering(false);
      setUser(!!Cookies.get('grocerylist_auth_token'));
    } catch (error) {
      setStatus(false);
      toast({
        description: doctorError(error.message),
        status: 'error',
        duration: 9001,
        isClosable: true
      });
    }
  };

  return (
    <Stack spacing="0.5rem" paddingBottom="2rem">
      <Flex
        justify="center"
        flexDirection="column"
        margin="auto"
        minWidth="300px"
        maxWidth="400px"
      >
        <form method="post" onSubmit={handleSubmit}>
          <StyledInput
            type="email"
            placeholder="Enter email"
            ref={emailRef}
            isDisabled={status}
          />

          {isRegistering && (
            <StyledInput
              type="text"
              placeholder="Enter display name"
              ref={nameRef}
              isDisabled={status}
            />
          )}

          <InputGroup size="md">
            <StyledInputPW
              pr="4.5rem"
              type={showPassword ? 'text' : 'password'}
              placeholder="Enter password"
              ref={passwordRef}
              isDisabled={status}
              error={error}
            />
            <InputRightElement width="4.5rem">
              <Button h="1.75rem" size="sm" onClick={togglePassword}>
                {showPassword ? 'Hide' : 'Show'}
              </Button>
            </InputRightElement>
          </InputGroup>

          {isRegistering && (
            <InputGroup size="md">
              <StyledInputPW
                pr="4.5rem"
                type={showPassword ? 'text' : 'password'}
                placeholder="Confirm password"
                ref={passwordConfirmationRef}
                isDisabled={status}
                error={error}
              />
              <InputRightElement width="4.5rem">
                <Button h="1.75rem" size="sm" onClick={togglePassword}>
                  {showPassword ? 'Hide' : 'Show'}
                </Button>
              </InputRightElement>
            </InputGroup>
          )}

          <SimpleGrid columns="2" gridGap="5px">
            <StyledButton
              variantColor="blue"
              icon={isRegistering ? AiOutlineDoubleLeft : AiOutlineForm}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              aria-label="Register for an account"
              onClick={toggleRegister}
            />
            <StyledButton
              type="submit"
              variantColor="blue"
              icon={FiLogIn}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              aria-label="Log into your account"
            />
          </SimpleGrid>
        </form>
      </Flex>
    </Stack>
  );
};

export default SignInForm;
