// Dependencies
import { useRef, useState } from 'react';
import { useMutation } from '@apollo/react-hooks';
import {
  Button,
  Flex,
  InputGroup,
  InputRightElement,
  SimpleGrid,
  Stack
} from '@chakra-ui/core';

// Styles
import { AiOutlineForm, AiOutlineDoubleLeft } from 'react-icons/ai';
import { FiLogIn } from 'react-icons/fi';
import { StyledButton, StyledInput } from './styles/styled';

// Helpers
import { REGISTER_USER_MUTATION } from '../lib/mutations';

const SignInForm = () => {
  // Form Refs
  const emailRef = useRef('');
  const nameRef = useRef('');
  const passwordRef = useRef('');
  const passwordConfirmationRef = useRef('');

  // Hooks
  const [status, setStatus] = useState(false);
  const [showPassword, setShow] = useState(false);
  const [registering, setRegistering] = useState(false);

  // Apollo
  const [register, registerRes] = useMutation(REGISTER_USER_MUTATION);

  const toggleRegister = () => {
    setShow(!showPassword);
  };

  const handleSubmit = (e) => {
    setStatus(true);
    e.preventDefault();

    if (registering) {
      const input = {
        email: emailRef.current.value,
        name: nameRefemailRef.current.value,
        password: passwordRefemailRef.current.value,
        passwordConfirmation: passwordConfirmationRefemailRef.current.value
      };

      register({ variables: { input } });
    }
    setStatus(true);
  };

  return (
    <Stack spacing="2rem" paddingBottom="2rem">
      <Flex justify="center">
        <form method="post" onSubmit={handleSubmit}>
          <StyledInput
            type="email"
            placeholder="Enter email"
            ref={emailRef}
            isDisabled={status}
          />

          {registering && (
            <StyledInput
              type="text"
              placeholder="Enter display name"
              ref={nameRef}
              isDisabled={status}
            />
          )}

          <InputGroup size="md">
            <StyledInput
              pr="4.5rem"
              type={showPassword ? 'text' : 'password'}
              placeholder="Enter password"
              ref={passwordRef}
              isDisabled={status}
            />
            <InputRightElement width="4.5rem">
              <Button h="1.75rem" size="sm" onClick={toggleRegister}>
                {showPassword ? 'Hide' : 'Show'}
              </Button>
            </InputRightElement>
          </InputGroup>

          {registering && (
            <InputGroup size="md">
              <StyledInput
                pr="4.5rem"
                type={showPassword ? 'text' : 'password'}
                placeholder="Confirm password"
                ref={passwordConfirmationRef}
                isDisabled={status}
              />
              <InputRightElement width="4.5rem">
                <Button h="1.75rem" size="sm" onClick={toggleRegister}>
                  {showPassword ? 'Hide' : 'Show'}
                </Button>
              </InputRightElement>
            </InputGroup>
          )}

          <SimpleGrid columns="2" gridGap="5px">
            <StyledButton
              variantColor="blue"
              icon={registering ? AiOutlineDoubleLeft : AiOutlineForm}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              aria-label="Register for an account"
              onClick={() => setRegistering(!registering)}
            />
            <StyledButton
              type="submit"
              variantColor="blue"
              icon={FiLogIn}
              isLoading={status}
              isDisabled={status}
              aira-busy={status}
              aria-label="Log into your account"
            />
          </SimpleGrid>
        </form>
      </Flex>
    </Stack>
  );
};

export default SignInForm;
