import React from 'react';
import styled from '@emotion/styled';
import { IconButton, Input } from '@chakra-ui/core';

export const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInputPW = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const NiceButton = ({ type, status, icon, label, height, onClick }) => (
  <StyledButton
    type={type && 'submit'}
    variantColor="blue"
    icon={icon}
    isLoading={status}
    isDisabled={status}
    aira-busy={status}
    aira-label={label}
    height={height}
  />
);
