import Link from 'next/link';
import { useRouter } from 'next/router';

import { Flex, List, ListItem, Heading } from '@chakra-ui/core';

const Nav = () => {
  const router = useRouter();

  return (
    <List as={Flex} justifyContent="space-evenly" paddingBottom="2rem">
      <ListItem>
        {router.pathname === '/history' ? (
          <Heading as="h1" size="2xl" cursor="pointer">
            History
          </Heading>
        ) : (
          <Link href="/history">
            <a>
              <Heading as="h1" size="2xl">
                History
              </Heading>
            </a>
          </Link>
        )}
      </ListItem>
      <ListItem>
        <Link href="/">
          <a>
            <Heading as="h1" size="2xl">
              Home
            </Heading>
          </a>
        </Link>
      </ListItem>
    </List>
  );
};

export default Nav;
