// Dependencies
import { useRouter } from 'next/router';

// Styles
import { AccordionIcon, AccordionItem, Stack } from '@chakra-ui/core';

const HistoryLoad = () => (
  <Skeleton borderRadius="1rem" marginBottom="1rem">
    <AccordionItem>
      <AccordionHeader>
        <Box flex="1" textAlign="left">
          <Text>Yes</Text>
          <Text>Yes</Text>
        </Box>
        <AccordionIcon />
      </AccordionHeader>
      <AccordionPanel pb={4}></AccordionPanel>
    </AccordionItem>
  </Skeleton>
);

const GroceryListLoad = () => (
  <Skeleton borderRadius="1rem">Placeholder</Skeleton>
);

const Loading = () => {
  const { pathname } = useRouter();

  if (pathname === '/history') {
    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <HistoryLoad />
        <HistoryLoad />
        <HistoryLoad />
        <HistoryLoad />
      </Stack>
    );
  }

  if (pathname === '/') {
    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <GroceryListLoad />
        <GroceryListLoad />
        <GroceryListLoad />
        <GroceryListLoad />
      </Stack>
    );
  }
};

export default Loading;
