// Dependencies
import { useQuery } from '@apollo/react-hooks';

// Styles
import { Heading, Stack, useToast } from '@chakra-ui/core';

// Apollo
import { USER_HISTORY_QUERY } from '../lib/apollo/queries';

// Components
import Loading from './Loading';
import HistoryList from './HistoryList';

const HistoryLayout = () => {
  // Hooks
  const toast = useToast();

  // Apollo
  const { data, loading, error } = useQuery(USER_HISTORY_QUERY);

  if (error) {
    toast({
      description: error,
      status: 'error',
      duration: 9001,
      isClosable: true
    });
  }

  if (loading) {
    return (
      <Loading>
        <AccordionItem>
          <AccordionHeader>
            <Box flex="1" textAlign="left">
              <Text fontSize="2xl">Order No. {id}</Text>
              <Text fontSize="lg">
                {formatDistance(Date.parse(createdAt), new Date(), {
                  addSuffix: true
                })}
              </Text>
            </Box>
            <AccordionIcon />
          </AccordionHeader>
          <AccordionPanel pb={4} isOpen={isOpen}>
            {isOpen && (
              <SimpleGrid columns="2">
                <Heading as="h4" size="md">
                  Item
                </Heading>
                <Heading as="h4" size="md">
                  Quantity
                </Heading>
                {history.map((item, index) => (
                  <HistoryItem key={index} {...item} />
                ))}
              </SimpleGrid>
            )}
          </AccordionPanel>
        </AccordionItem>
      </Loading>
    );
  }

  if (data?.currentUserHistory.nodes.length) {
    const list = data.currentUserHistory.nodes;

    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        {list.map((item) => (
          <HistoryList key={item.createdAt} {...item} />
        ))}
      </Stack>
    );
  } else {
    return (
      <Heading as="h4" size="lg">
        Remember to add to your list. üìù
      </Heading>
    );
  }
};

export default HistoryLayout;
