// Dependnencies
import { useMutation, useQuery } from '@apollo/react-hooks';

// Helpers
import { ALL_ITEMS_QUERY } from '../lib/apollo/queries';
import { REMOVE_ITEM_MUTATION } from '../lib/apollo/mutations';

// Styles
import { Heading, List, ListItem, Skeleton, SimpleGrid } from '@chakra-ui/core';

// Components
import Item from './Item';
import GroceryListLayout from './layouts/GroceryListLayout';

const GroceryList = () => {
  // Apollo
  const { data, loading, error } = useQuery(ALL_ITEMS_QUERY);
  const [deleteItem, deleteRes] = useMutation(REMOVE_ITEM_MUTATION, {
    update(cache, { data: { deleteItem } }) {
      const {
        items: { nodes }
      } = cache.readQuery({ query: ALL_ITEMS_QUERY });

      const { id } = deleteItem.item;
      cache.writeQuery({
        query: ALL_ITEMS_QUERY,
        data: { items: { nodes: nodes.filter((node) => node.id !== id) } }
      });
    }
  });

  // Handlers
  const handleClick = (id) => {
    deleteItem({ variables: { input: { id } } });
  };

  if (error) console.log('error:', JSON.parse(JSON.stringify(error)));

  const loadingV2 = true;

  if (loadingV2)
    return (
      <List width="60%" maxWidth="500px" minWidth="300px" margin="auto">
        <ListItem paddingBottom="0.5rem">
          <Heading as="h4" size="lg">
            Loading...
          </Heading>
        </ListItem>
        <Skeleton as={Heading} as="h4" />
        <Skeleton as={Heading} as="h4" />
        <Skeleton as={Heading} as="h4" />
        <Skeleton as={Heading} as="h4" />
      </List>
    );

  if (!loading && !error && data.items.nodes && data.items.nodes.length > 0) {
    return (
      <React.Fragment>
        {/* <p>Error: {error.message}</p> */}
        <GroceryListLayout>
          {data.items.nodes.map((item) => (
            <Item
              key={item.id + item.product}
              id={item.id}
              product={item.product}
              quantity={item.quantity}
              handleClick={handleClick}
            />
          ))}
        </GroceryListLayout>
      </React.Fragment>
    );
  } else {
    return (
      <React.Fragment>
        {/* <p>Error: {error.message}</p> */}
        <Heading as="h4" size="lg">
          No groceries.
        </Heading>
      </React.Fragment>
    );
  }
};

export default GroceryList;
