import { createContext, useState } from 'react';

const ViewContext = createContext({});

const View = (props) => {
  const [isAdd, setIsAdd] = useState(true);

  return (
    <ViewContext.Provider value={{ isAdd, setIsAdd }}>
      {props.children}
    </ViewContext.Provider>
  );
};

export default View;

export { ViewContext };
