import { SimpleGrid, ListItem, CloseButton, Flex, Text } from '@chakra-ui/core';

const Item = ({ id, product, quantity }) => (
  <ListItem>
    <SimpleGrid columns="3">
      <Text alignSelf="center" as="b">
        {product}
      </Text>
      <Text alignSelf="center" as="b">
        {quantity}
      </Text>
      <Flex justify="center">
        <CloseButton />
      </Flex>
    </SimpleGrid>
  </ListItem>
);

export default Item;
