// Dependencies
import { useContext, useState } from 'react';

// Styles
import { Flex, IconButton, SimpleGrid, Text } from '@chakra-ui/core';
import { MdRemoveCircleOutline, MdLabel, MdLabelOutline } from 'react-icons/md';

// Context
import { ViewContext } from '../lib/React/ViewContext';
import { StatusContext } from '../lib/React/StatusContext';

const Item = ({ id, product, quantity, handleClick }) => {
  const { addToHistory, removeFromHistory } = useContext(StatusContext);
  const { isAdd } = useContext(ViewContext);
  const [selected, setSelected] = useState(false);

  const changeStatus = () => {
    if (selected) removeFromHistory(id);
    else addToHistory(id, !status);

    setSelected(!selected);
  };

  return (
    <SimpleGrid columns="3" paddingBottom="0.25rem">
      <Text alignSelf="center" as="b">
        {product}
      </Text>
      <Text alignSelf="center" as="b">
        {quantity}
      </Text>
      <Flex justify="center">
        {isAdd ? (
          <IconButton
            size="sm"
            variantColor="blue"
            icon={MdRemoveCircleOutline}
            onClick={() => handleClick(id)}
            aria-label="Click to remove from list"
          />
        ) : (
          <IconButton
            size="sm"
            variantColor="blue"
            icon={selected ? MdLabel : MdLabelOutline}
            onClick={changeStatus}
            aria-label={`Click to select`}
          />
        )}
      </Flex>
    </SimpleGrid>
  );
};
export default Item;
