// Dependencies
import { useQuery } from '@apollo/react-hooks';

// Styles
import { Box, Heading, SimpleGrid, Stack, useToast } from '@chakra-ui/core';

// Helpers
import { doctorError } from '../lib/doctorError';

// Apollo
import { USER_HISTORY_QUERY } from '../lib/apollo/queries';

// Components
import Loading from './Loading';
import HistoryItem from './HistoryItem';

const HistoryList = () => {
  // Hooks
  const toast = useToast();

  // Apollo
  const { data, loading, error } = useQuery(USER_HISTORY_QUERY);

  if (error) {
    toast({
      description: error,
      status: 'error',
      duration: 9001,
      isClosable: true
    });
  }

  if (loading) {
    return <Loading />;
  }

  if (data?.currentUserHistory.nodes.length) {
    const list = data.currentUserHistory.nodes;

    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <SimpleGrid columns="3">
          <Heading as="h4" size="lg">
            Item
          </Heading>
          <Heading as="h4" size="lg">
            Quantity
          </Heading>
        </SimpleGrid>
        <Box>
          {list.map((item) => {
            console.log('item');
            console.log(item);

            return (
              <HistoryItem
                key={item.id + item.product}
                id={item.id}
                product={item.product}
                quantity={item.quantity}
              />
            );
          })}
        </Box>
      </Stack>
    );
  } else {
    return (
      <Heading as="h4" size="lg">
        Remember to add to your list. üìù
      </Heading>
    );
  }
};

export default HistoryList;
