// Dependencies
import { useRef, useState } from 'react';
import { useMutation } from '@apollo/react-hooks';

// Styles
import { Box, Flex, Grid } from '@chakra-ui/core';
import { MdAddShoppingCart } from 'react-icons/md';

// Helpers
import { ADD_ITEM_MUTATION } from '../lib/apollo/mutations';
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { NiceButton, StyledButton, StyledInput } from './styles/styled';

const GroceryForm = () => {
  // Form Refs
  const productRef = useRef('');
  const quantityRef = useRef('');

  // Hooks
  const [isCalling, setIsCalling] = useState(false);

  // Apollo
  const [addItem] = useMutation(ADD_ITEM_MUTATION);

  // Handlers
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsCalling(true);

    const input = {
      product: productRef.current.value,
      quantity: parseInt(quantityRef.current.value)
    };

    addItem({
      variables: { input },
      optimisticResponse: {
        __typename: 'Mutation',
        addItem: {
          item: {
            id: Math.round(Math.random() * -1000000),
            ...input
          }
        }
      },
      update: (cache, { data: { addItem } }) => {
        const { currentUserItems } = cache.readQuery({
          query: USERS_ITEMS_QUERY
        });
        const { nodes } = currentUserItems;
        const { item } = addItem;

        cache.writeQuery({
          query: USERS_ITEMS_QUERY,
          data: { currentUserItems: { nodes: [item, ...nodes] } }
        });
      }
    });

    setIsCalling(false);
    productRef.current.value = '';
    quantityRef.current.value = '';

    productRef.current.focus();
  };

  return (
    <Flex justify="center">
      <form method="post" onSubmit={handleSubmit}>
        <Grid templateColumns="5fr 1fr">
          <Box>
            <StyledInput
              type="text"
              placeholder="Product"
              isDisabled={isCalling}
              ref={productRef}
              isRequired
            />
            <StyledInput
              type="number"
              placeholder="Quantity"
              isDisabled={isCalling}
              ref={quantityRef}
              isRequired
            />
          </Box>
          <StyledButton
            type="submit"
            variantColor="blue"
            icon={MdAddShoppingCart}
            isLoading={isCalling}
            isDisabled={isCalling}
            aira-busy={isCalling}
            aira-label="Add into to your grocery list"
            height
          />
          <NiceButton
            type="submit"
            variantColor="blue"
            icon={MdAddShoppingCart}

            aira-busy={isCalling}
            aira-label="Add into to your grocery list"
            height
          />
        </Grid>
      </form>
    </Flex>
  );
};

export default GroceryForm;
