import Link from 'next/link';
import {
  Box,
  Button,
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
  Text,
  useDisclosure,
  List,
  ListItem
} from '@chakra-ui/core';

const SideInfo = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();

  return (
    <Box position="absolute" top="0" right="0">
      <Button ref={btnRef} variantColor="teal" onClick={onOpen}>
        About Me
      </Button>
      <Drawer
        isOpen={isOpen}
        placement="right"
        onClose={onClose}
        finalFocusRef={btnRef}
        size="md"
      >
        <DrawerOverlay />
        <DrawerContent>
          <DrawerCloseButton />
          <DrawerHeader borderBottomWidth="1px">About Me
          <Text>I'm Eric Lau a full sta</Text>
          </DrawerHeader>

          <DrawerBody>
            <Text>Welcome to my grocery list web app!</Text>
            <List styleType="disc">
              <Text>Powered by:</Text>
              <ListItem paddingLeft="1rem">Next.js</ListItem>
              <ListItem paddingLeft="1rem">GraphQL</ListItem>
              <ListItem paddingLeft="1rem">PostGraphile</ListItem>
              <ListItem paddingLeft="1rem">What</ListItem>
            </List>
          </DrawerBody>
        </DrawerContent>
      </Drawer>
    </Box>
  );
};

export default SideInfo;
