// Dependnencies
import { useEffect, useState } from 'react';
import { useMutation, useQuery } from '@apollo/react-hooks';

// Helpers
import { USERS_ITEMS_QUERY } from '../lib/apollo/queries';
import { REMOVE_ITEM_MUTATION } from '../lib/apollo/mutations';
import { doctorError } from '../lib/doctorError';

// Styles
import {
  Heading,
  Skeleton,
  SimpleGrid,
  Stack,
  useToast,
  Text
} from '@chakra-ui/core';

// Components
import Item from './Item';

const GroceryList = () => {
  // Hooks
  const toast = useToast();
  const [dummy, setdummy] = useState(initialState)


  // Apollo
  const { data, loading, error } = useQuery(USERS_ITEMS_QUERY);
  const [deleteItem, deleteResponse] = useMutation(REMOVE_ITEM_MUTATION);

  // Handlers
  const handleClick = async (id) => {
    try {
      await deleteItem({
        variables: { input: { id } },
        optimisticResponse: {
          __typename: 'Mutation',
          deleteItem: {
            item: { id }
          }
        },
        update: (cache, { data: { deleteItem } }) => {
          const { currentUser } = cache.readQuery({ query: USERS_ITEMS_QUERY });
          const { nodes } = currentUser.itemsByOwnerId;
          const { id } = deleteItem.item;

          cache.writeQuery({
            query: USERS_ITEMS_QUERY,
            data: {
              currentUser: {
                itemsByOwnerId: {
                  nodes: nodes.filter((node) => node.id !== id)
                }
              }
            }
          });
        }
      });
    } catch (error) {
      // need to catch the error, so a popup doesn't show up
    }
  };

  if (error || deleteResponse.error) {
    if (!!error) {
      toast({
        description: error,
        status: 'error',
        duration: 9001,
        isClosable: true
      });
    }

    if (!!deleteResponse.error) {
      toast({
        description: doctorError(deleteResponse.error.message),
        status: 'error',
        duration: 9001,
        isClosable: true
      });
    }
  }

  if (loading)
    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <Heading as="h4" size="lg">
          Loading
        </Heading>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
        <Skeleton borderRadius="1rem">Placeholder</Skeleton>
      </Stack>
    );

  if (data.currentUser?.itemsByOwnerId.nodes.length) {
    const list = data.currentUser.itemsByOwnerId.nodes;

    return (
      <Stack
        width="60%"
        maxWidth="500px"
        minWidth="300px"
        margin="auto"
        spacing="0.5rem"
      >
        <SimpleGrid columns="3">
          <Heading as="h4" size="lg">
            Item
          </Heading>
          <Heading as="h4" size="lg">
            Quantity
          </Heading>
          <Text></Text>
        </SimpleGrid>
        <SimpleGrid columns="3">
          {list.map((item) => (
            <Item
              key={item.id + item.product}
              id={item.id}
              product={item.product}
              quantity={item.quantity}
              handleClick={handleClick}
            />
          ))}
        </SimpleGrid>
      </Stack>
    );
  } else {
    return (
      <Heading as="h4" size="lg">
        Remember to add to your list. üìù
      </Heading>
    );
  }
};

export default GroceryList;
