import React, { forwardRef, Component } from 'react';
import styled from '@emotion/styled';
import { IconButton, Input } from '@chakra-ui/core';

export const StyledButton = styled(IconButton)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInput = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const StyledInputPW = styled(Input)`
  :disabled {
    opacity: 1;
  }
`;

export const InputField = forwardRef(
  ({ text, placeholder, isCalling }, ref) => {
    return (
      <StyledInput
        type={text ? text : 'text'}
        placeholder={placeholder ? placeholder : 'Product'}
        isDisabled={isCalling}
        ref={ref}
        isRequired
      />
    );
  }
);
