import { SimpleGrid, ListItem, CloseButton, Flex, Text } from '@chakra-ui/core';
import { MdCheck } from 'react-icons/md';

import useView from '../lib/useView';

const Item = ({ id, product, quantity, handleClick }) => {
  const [isAdd, setIsAdd] = useView();

  return (
    <ListItem>
      <SimpleGrid columns="3">
        <Text alignSelf="center" as="b">
          {product}
        </Text>
        <Text alignSelf="center" as="b">
          {quantity}
        </Text>
        <Flex justify="center">
          <CloseButton onClick={() => handleClick(id)} />
        </Flex>
      </SimpleGrid>
    </ListItem>
  );
};
export default Item;
