import { useQuery } from '@apollo/react-hooks';
import { ALL_ITEMS_QUERY } from '../lib/queries';
import Item from './Item';

const GroceryList = () => {
  const { data, loading, error } = useQuery(ALL_ITEMS_QUERY);

  if (error) return <p>Error: {error}</p>;
  if (loading) return <p>Loading...</p>;

  const items = data.items.nodes;

  return (
    <React.Fragment>
      {items.length > 0 && (
        <ul>
          {items.map((item) => (
            <Item key={item.id} product={item.product} quantity />
          ))}
        </ul>
      )}
    </React.Fragment>
  );
};

export default GroceryList;
